import React, { useEffect } from 'react';
import { Link } from 'react-scroll';
import LogoWhite from '../../assets/images/logo_white.svg';
import './header.css';

function Navbar() {
  useEffect(() => {
    const navLinks = document.querySelector('.nav-links');
    const navIcon = document.getElementById('nav-icon');

    const handleNavIconClick = () => {
        navLinks.classList.toggle('active');
        navIcon.textContent = navLinks.classList.contains('active') ? '✕' : '☰';
    };

    const handleNavLinkClick = (e) => {
        e.preventDefault();
        const targetId = e.target.getAttribute('href').substring(1);
        const targetElement = document.getElementById(targetId);
        const navbarHeight = document.querySelector('.navbar').offsetHeight;

        if (navLinks.classList.contains('active')) {
            navLinks.classList.remove('active');
            navIcon.textContent = '☰';
        }

        smoothScroll(targetElement, navbarHeight);
    };

    const smoothScroll = (target, offset) => {
        const targetPosition = target.getBoundingClientRect().top + window.scrollY - offset;
        window.scrollTo({
            top: targetPosition,
            behavior: 'smooth',
        });
    };

    navIcon.addEventListener('click', handleNavIconClick);
    document.querySelectorAll('.nav-links a').forEach((anchor) => {
        anchor.addEventListener('click', handleNavLinkClick);
    });

    return () => {
        navIcon.removeEventListener('click', handleNavIconClick);
        document.querySelectorAll('.nav-links a').forEach((anchor) => {
            anchor.removeEventListener('click', handleNavLinkClick);
        });
    };
  }, []);

return () => {
    navIcon.removeEventListener('click', handleNavIconClick);
    document.querySelectorAll('.nav-links a').forEach((anchor) => {
        anchor.removeEventListener('click', handleNavLinkClick);
    });
};

  return (
    <nav className="navbar" id="navbar">
      <div className="logo">
        <Link to="./App.js" smooth={true} duration={500} offset={-120}>
          <img src={LogoWhite} alt="logo" className="logo-img" id="logo-img" />
        </Link>
      </div>
      <div className="nav-links">
        <ul>
          <li><Link to="Progetto" smooth={true} duration={500} offset={-120}>Il progetto</Link></li>
          <li><Link to="Storia" smooth={true} duration={500} offset={-120}>La storia</Link></li>
          <li><Link to="Soluzioni-abitative" smooth={true} duration={500} offset={-120}>Soluzioni abitative</Link></li>
          <li className="prenota-container"><Link to="Contatti" className="prenota" smooth={true} duration={500} offset={-120}>PRENOTA UN APPUNTAMENTO</Link></li>
        </ul>
        <Link to="Contatti" className="contact-btn2" smooth={true} duration={500} offset={-120}>CONTATTACI</Link>
      </div>
      <div className="nav-icon" id="nav-icon">☰</div>
    </nav>
  );
}

export default Navbar;
