import React, { useState } from 'react';
import axios from 'axios';
import './contatti.css';

function Form(){
    const [formData, setFormData] = useState({
        name: '',
        lastName: '',
        email: '',
        phone: '',
        tipologies: '',
        message: '',
        accettaInformativa: false,
        accettaServizi: false,
        accettaMarketing: false,
    });

    const [statusMessage, setStatusMessage] = useState(null);

    const handleChange = (e) => {
        const { name, value, type, checked } = e.target;
        setFormData({
          ...formData,
          [name]: type === 'checkbox' ? checked : value,
        });
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        axios.post('http://localhost/back-end/api/contact.php', formData)
          .then(response => {
            setStatusMessage({
              type: 'success',
              text: 'Dati inviati con successo!',
            });
            setFormData({
              name: '',
              lastName: '',
              email: '',
              phone: '',
              tipologies: '',
              message: '',
              accettaInformativa: false,
              accettaServizi: false,
              accettaMarketing: false,
            });
          })
          .catch(error => {
            setStatusMessage({
              type: 'error',
              text: 'Errore nell\'invio dei dati!',
            });
          });
    };

    return(
        <form id="form" className="form" onSubmit={handleSubmit}>
            <div className="campo-form">
                <div className="label-form" id="name-label">NOME*</div>
                <input type="text" name="name" id="name" maxLength={256} aria-labelledby="name-label" required onChange={handleChange}/>
            </div>
            <div className="campo-form">
                <div className="label-form" id="last-name-label">COGNOME*</div>
                <input type="text" name="last-name" maxLength={256} id="last-name" aria-labelledby="last-name-label" required onChange={handleChange}/>
            </div>
            <div className="campo-form">
                <div className="label-form" id="email-label">EMAIL*</div>
                <input type="email" name="email" maxLength={256} id="email" aria-labelledby="email-label" required onChange={handleChange}/>
            </div>
            <div className="campo-form">
                <div className="label-form" id="phone-label">TELEFONO*</div>
                <input type="text" name="phone" maxLength={256} id="phone" aria-labelledby="phone-label" required onChange={handleChange}/>
            </div>
            <div className="campo-form">
                <div className="label-form" id="tipologies-label">TIPOLOGIE</div>
                <select id="tipologies" name="tipologies" style={{color: '#fff'}} aria-labelledby="tipologies-label" onChange={handleChange}>
                    <option value="">Seleziona</option>
                    <option value="Loft">Loft</option>
                    <option value="Bilocale">Bilocale</option>
                    <option value="Trilocale">Trilocale</option>         
                </select>
            </div>
            <div className="campo-form">
                <div className="label-form" id="message-label">MESSAGGIO</div>
                <textarea placeholder="" maxLength={5000} id="message" name="message" aria-labelledby="message-label" onChange={handleChange}></textarea>
            </div>
            <div className="checkbox-group">
                <input type="checkbox" id="accetta-informativa" name="accetta-informativa" required onChange={handleChange}/>
                <div htmlFor="accetta-informativa">Dichiaro di aver ricevuto idonea informativa e di prestare il suo consenso, esplicito ed inequivocabile, in relazione alle finalit√† del trattamento, come espresse al punto 3, lettera b, dell'informativa per il trattamento dei dati</div>
            </div>
            <div className="checkbox-group">
                <input type="checkbox" id="accetta-servizi" name="accetta-servizi" required onChange={handleChange}/>
                <div htmlFor="accetta-servizi">Acconsento alla registrazione dei suoi dati per ricevere servizio personalizzato sulle sue reali esigenze d'acquisto</div>
            </div>
            <div className="checkbox-group">
                <input type="checkbox" id="accetta-marketing" name="accetta-marketing" onChange={handleChange}/>
                <div htmlFor="accetta-marketing">Acconsento all'invio di comunicazioni di marketing, per mezzo di sistemi informatici automatizzati, ivi inclusi comunicazioni commerciali o promozionali a mezzo email o SMS, ovvero per ricerche ed analisi di mercato</div>
            </div>
            <button className="submit" id="submit" type="submit">INVIA RICHIESTA</button>
            {statusMessage && (
                <div style={{
                    backgroundColor: statusMessage.type === 'success' ? 'lightgreen' : 'lightcoral',
                    padding: '10px',
                    marginTop: '10px',
                    borderRadius: '5px',
                    color: '#1a1a1a'
                    }}>
                    {statusMessage.text}
                </div>
            )}
        </form>
    );
}

export default Form;
